summaries.response:
{
  "feature": "Instagram Integration",
  "steps": [
    {
      "step_number": 1,
      "description": "Obtain API credentials from Instagram Developer Platform.",
      "details": {
        "client_id": "YOUR_CLIENT_ID",
        "client_secret": "YOUR_CLIENT_SECRET",
        "redirect_uri": "YOUR_REDIRECT_URI"
      }
    },
    {
      "step_number": 2,
      "description": "Authenticate users using OAuth 2.0.",
      "details": {
        "authorization_url": "https://api.instagram.com/oauth/authorize",
        "token_url": "https://api.instagram.com/oauth/access_token"
      }
    },
    {
      "step_number": 3,
      "description": "Retrieve user's Instagram data.",
      "details": {
        "api_endpoint": "https://api.instagram.com/v1/users/self/",
        "scope": "basic_info,photos"
      }
    },
    {
      "step_number": 4,
      "description": "Display user's Instagram feed in your application.",
      "details": {
        "api_endpoint": "https://api.instagram.com/v1/users/self/media/recent/",
        "display_options": "thumbnails,likes,captions"
      }
    }
  ]
}
